import { useEffect, useState } from "react";
import { getAllAccounts, createAccount } from "./services/api";

function App() {
  const [accounts, setAccounts] = useState([]);//creating empty array
  //below insterting the data fetched from backend
  const [formData, setFormData] = useState({
    accountHolderName: "",
    accountType: "savings",
    balance: ""
  });

  useEffect(() => {
    fetchAccounts();
  }, []);

  const fetchAccounts = () => {
	getAllAccounts().then(data => {
	console.log("Fetched accounts:", data);
	if (Array.isArray(data)) {
		setAccounts(data);
	} else if (data && Array.isArray(data.content)) {
	// Handle paged response like { content: [...], totalPages: X }
		setAccounts(data.content);
	} else {
		console.error("Unexpected data format:", data);
		setAccounts([]); // avoid crashing UI
	}
  }).catch(err => {
  console.error("Error fetching accounts:", err);
  setAccounts([]); // fallback
  });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    createAccount(formData).then(() => {
      fetchAccounts(); // refresh list
      setFormData({
        accountHolderName: "",
        accountType: "savings",
        balance: ""
      });
    });
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Create New Account</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: "2rem" }}>
        <input
          name="accountHolderName"
          value={formData.accountHolderName}
          onChange={handleChange}
          placeholder="Account Holder Name"
          required
        />
        <select
          name="accountType"
          value={formData.accountType}
          onChange={handleChange}
          style={{ marginLeft: "1rem" }}
        >
          <option value="savings">Savings</option>
          <option value="current">Current</option>
        </select>
        <input
          name="balance"
          value={formData.balance}
          onChange={handleChange}
          type="number"
          placeholder="Initial Balance"
          style={{ marginLeft: "1rem" }}
          required
        />
        <button type="submit" style={{ marginLeft: "1rem" }}>Create</button>
      </form>

      <h2>Existing Accounts</h2>
      <ul>
        {Array.isArray(accounts) && accounts.map((account) => ( 
          <li key={account.id}>
            {account.accountHolderName} — {account.accountType} — ₹{account.balance} 
		  </li> 
		  ))
		 } 
	  </ul>
    </div>
  );
}

export default App;
